sp_angular_provider.script:
function teamonlineMomentCommentForm() {
	function link(controller) {
		var c = controller
		c.comment = 'asasas'		
	}

	function controller($rootScope) {
		var ctrl = this;
		ctrl.send = () => {
			console.log('sending data:: ', ctrl.parent, ctrl.moment, ctrl.comment)
			$rootScope.$broadcast('commentSent', { comment: ctrl.comment, parent: ctrl.parent, moment: ctrl.moment })
		}

		// ctrl.$on('commentSelected', (evt, data) => {
		// 	ctrl.parent = data.comment
		// 	ctrl.comment = '@'+ctrl.parent.author.userId + ', ' +comment
		// })
	}
	return {
		templateUrl: function (element, attrs) {
			return attrs.templateUrl;
		},
		restrict: 'E',
		replace: true,
		bindToController: {
			moment: '='
		},
		controller,
		controllerAs: 'ctrl',
		link

	}
}
