sp_angular_provider.script:
function teamonlineMomentCommentForm() {
	function link(controller) {
		var c = controller
		c.comment = ''
	}

	function controller($rootScope) {
		var ctrl = this;
		ctrl.comment = '';
		ctrl.send = () => {
			const payload = {
				comment: ctrl.comment, 
				parent: ctrl.parent?ctrl.parent.sys_id : '', 
				moment: ctrl.moment.data.sys_id 
			}
			$rootScope.$broadcast('commentSent', payload)
		}
		$rootScope.$on('replyToRoot', (evt, data) => {
			ctrl.comment = '@' + data.comment.author.id + ', '+ ctrl.comment;
			ctrl.parent = data.comment;
		});
		$rootScope.$on('replyToChild', (evt, data) => {
			ctrl.comment = '@' + data.comment.author.id + ', '+ ctrl.comment;
			ctrl.parent = '';
		});
	}
	return {
		templateUrl: function (element, attrs) {
			return attrs.templateUrl;
		},
		restrict: 'E',
		replace: true,
		bindToController: {
			moment: '='
		},
		controller,
		controllerAs: 'ctrl',
		link

	}
}
