sp_widget.client_script:
function($scope, $rootScope, spUtil) {
  /* widget controller */
  var c = this;

  $rootScope.$on('momentApprovalAction', (evt, data) => {
    approveAction(data.sys_id, data.state, data.isArchive)
  })
  function approveAction(sys_id, state, isArchive) {
    $rootScope.$broadcast('loadingStart')
    const payload = {
      approval: sys_id,
      approveState: state
    }
    c.server.get(payload).then((resp) =>{
      spUtil.addInfoMessage('Moment successfully ' + (isArchive ? 'archived' : state))
      $scope.data.moments = resp.data.moments
      $rootScope.$broadcast('loadingEnd')
    })
  }

  spUtil.recordWatch($scope, $scope.data.momentsTable, "approval=requested", () => {
			c.server.get({}).then(resp => {
				$scope.data.moments = resp.data.moments;
		})
	})
}

sp_widget.css:
/*all styles moved to shared css include moments.css*/
