sp_widget.client_script:
function($scope, spModal, spUtil) {
  /* widget controller */
  var c = this;
  $scope.loading = false;
  c.lastPage = false;
  c.filter = 'all'
  c.customFilterCollapsed = true;
  $scope.storeFilter = { name: 'storeFilter', displayValue: '', value: '' }
  $scope.userFilter = { name: 'userFilter', displayValue: '', value: '' }
  $scope.captionFilter = ''
  $scope.previousDateTime = '';

  c.loadMore = (last, inview) => {

    if (!(last && inview) || c.lastPage) return;

    const payload = {
      lastDateTime: c.data.moments[c.data.moments.length - 1].data.created_on,
      filter: c.filter,
      count: 3,
      user: c.user,
      store: c.store,
      caption: $scope.captionFilter, 
      action: 'loading'
    }
    if ($scope.previousDateTime === payload.lastDateTime) return;

    c.server.get(payload).then(resp => {
      $scope.previousDateTime = payload.lastDateTime;
      if (resp.data.nextMoments.length == 0)
        c.lastPage = true;

      c.data.moments = [...c.data.moments, ...resp.data.nextMoments]
    })
  }

  $scope.$on("field.change", function (evt, parms) {
    if (parms.field.name === 'storeFilter') {
      c.store = parms.newValue;
    }
    if (parms.field.name === 'userFilter') {
      c.user = parms.newValue
    }
  });
  $scope.setFilter = (filter) => {
    c.customFilterCollapsed = filter != 'custom';
    c.lastPage = false;
    scrollTop()
    c.filter = filter;
    $scope.loading = true;

    const payload = {
      filter: c.filter,
      count: 9,
      user: c.user,
      store: c.store,
      caption: $scope.captionFilter,
      action: 'loading',
    }
    c.server.get(payload).then(resp => {
      console.log(resp.data)
      console.log(resp.data.nextMoments.length)
      if (resp.data.nextMoments.length == 0) {
        c.data.moments = [];
        $scope.loading = false;
        return;
      }
      c.data.moments = resp.data.nextMoments;
      $scope.loading = false;

      c.loadMore(true, true)
    })
  }
  $scope.openCustomFilter = () => {
    c.customFilterCollapsed = !c.customFilterCollapsed;
    c.filter = 'custom'
  }
  $scope.openSubmitForm = () => {
    spModal.open({
      title: 'New moment',
      widget: c.data.submitFormWidget,
      widgetInput: { collapsed: false, showButton: false, source: 'grid' },
      buttons: [
        { label: 'Close', cancel: true }
      ]
    })
  }
  $scope.approve = (moment) => {
    approveAction(moment.data.sys_id, 'approved')
  }
  $scope.reject = (moment) => {

    approveAction(moment.data.sys_id, 'rejected')
  }
  $scope.archive = (moment)=>{
   approveAction(moment.sys_id, 'rejected', true)
  }
  function approveAction(sys_id, state, isArchive) {
    $scope.approving = true;
    const payload = {
      approval: sys_id,
      approveState: state,
      action: 'approval'
    }
    c.server.get(payload).then((resp) =>{
      $scope.approving = false;
      spUtil.addInfoMessage('Moment successfully ' + (isArchive ? 'archived' : state))
      $scope.data.moments = resp.data.moments
      $scope.data.approvalsCount = resp.data.approvalsCount
    })
  }
if($scope.data.canEdit)
spUtil.recordWatch($scope, $scope.data.momentsTable, "approval=requested", () => {
    c.server.get({action: 'getApprovalsCount'}).then(resp => {
      if(resp.data.approvalsCount > $scope.data.approvalsCount){
        spUtil.addInfoMessage('New moment has been added and is awaiting for approval')
      }
      $scope.data.approvalsCount = resp.data.approvalsCount;
  })
})
  var gallery = document.getElementsByClassName('moment-gallery')[0];
  $scope.openImage = function (momentIndex, imageIndex) {
    $scope.selectedMomentIndex = momentIndex;
    $scope.selectedMoment = $scope.data.moments[momentIndex]
    $scope.selectedImageIndex = imageIndex;
    gallery.classList.add('active')
  }
  $(document).keydown(function (e) {
    if (e.keyCode == 27) {
      $scope.closeGallery()
    }
  });
  function scrollTop() {
    var container = document.getElementsByClassName('moments-grid-container')[0];
    if (container)
      container.scrollTop = 0;

  }
  $scope.closeGallery = function () {
    $scope.selectedMoment = {}
    gallery.classList.remove('active');

  }
  $scope.selectImage = function (index) {
    $scope.selectedImageIndex = index
  }
  $scope.iterateMoment = function (amount) {
    var length = $scope.data.moments.length
    if ($scope.selectedMomentIndex + amount > length - 1) $scope.selectedMomentIndex = 0;
    else if ($scope.selectedMomentIndex + amount < 0) $scope.selectedMomentIndex = length - 1;
    else $scope.selectedMomentIndex += amount;
    $scope.selectedMoment = $scope.data.moments[$scope.selectedMomentIndex]
    $scope.selectedImageIndex = 0;
  }

  /* context Menu */
  $scope.contextMenu = function (moment) {
    if (!$scope.data.canEdit) { return [] }
    return [
      ['Archive', function ($itemScope) {
        var currentScope = getCurrentScopeByMethod($itemScope, 'archive');
        currentScope.archive(moment);
      }]
    ]
  }

  /* returs current scope */
  function getCurrentScopeByMethod(scope, method) {
    var currentScope = scope;
    var i = 0;
    while (!currentScope.hasOwnProperty(method) && i < 10) {
      currentScope = currentScope.$parent;
      i++;
    }
    return currentScope;
  }
}

